    //this methods aim is to show current date, it provides month,year and day via GregorianCalendar 

    public void currentDate() {
        Calendar cal = new GregorianCalendar();
        int month = cal.get(Calendar.MONTH);
        int year = cal.get(Calendar.YEAR);
        int day = cal.get(Calendar.DAY_OF_MONTH);

        txt_Date.setText("Date  " + year + "/" + (month + 1) + "/" + day);

        int second = cal.get(Calendar.SECOND);
        int minute = cal.get(Calendar.MINUTE);
        int hour = cal.get(Calendar.HOUR);

        txt_Time.setText("Time  " + hour + ":" + (minute) + ":" + second);

    }
    //To highlight the selected text we need to use MyHighlightPainter class

    class MyHighlightPainter extends DefaultHighlighter.DefaultHighlightPainter {

        public MyHighlightPainter(Color color) {
            super(color);
        }
    }
    //set the color you want it to highlight
    Highlighter.HighlightPainter myHighlightPainter = new MyHighlightPainter(Color.ORANGE);
    //removing last searched word's really important if we do not use this method, we always highlight all previous  words

    public void removeHighlights(JTextComponent textComp) {
        Highlighter hilite = textComp.getHighlighter();
        Highlighter.Highlight[] hilites = hilite.getHighlights();
        for (int i = 0; i < hilites.length; i++) {
            if (hilites[i].getPainter() instanceof MyHighlightPainter) {
                hilite.removeHighlight(hilites[i]);
            }
        }
    }
    //highlighting method, it works with an object of Highlighter

    public void highlight(JTextComponent textComp, String pattern) {
        removeHighlights(textComp);
        try {
            Highlighter hilite = textComp.getHighlighter();
            Document doc = textComp.getDocument();
            String text = doc.getText(0, doc.getLength());
            int pos = 0;
            //user can input upper or lowercase, to searrch both situation use that while loop
            while ((pos = text.toUpperCase().indexOf(pattern.toUpperCase(), pos)) >= 0) {

                hilite.addHighlight(pos, pos + pattern.length(), myHighlightPainter);
                pos += pattern.length();

            }

        } catch (Exception e) {

        }

    }

