    private void jMenuItem2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem2ActionPerformed
        //dynamic button is an object of DynamicButton created manually
        JFrame jFrame1 = new JFrame();
        jFrame1.setDefaultCloseOperation(WindowConstants.EXIT_ON_CLOSE);
        DynamicButton button = new DynamicButton("Test the internet connection button");
        //to make button dynamic, use actionListener
        button.addActionListener(new ActionListener() {
            public void actionPerformed(ActionEvent e) {
                System.out.println("I am clicked!");
                action();

            }
        });
        //container object is created to show that there is a button
        Container cont = jFrame1.getContentPane();
        cont.setLayout(new FlowLayout());
        cont.add(new JLabel("TEST ME:"));
        cont.add(button);
        jFrame1.pack();
        jFrame1.setVisible(true);


    }//GEN-LAST:event_jMenuItem2ActionPerformed

    private void jMenuItem3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem3ActionPerformed
       //piechart creating
        DefaultPieDataset pie = new DefaultPieDataset();
        pie.setValue("phone", 3);
        pie.setValue("computer", 6);
        pie.setValue("other", 4);
        JFreeChart chart = ChartFactory.createPieChart("Pie Chart Of Category ", pie, true, true, true);
        PiePlot p = (PiePlot) chart.getPlot();
        ChartFrame frame = new ChartFrame("Pie Chart", chart);
        frame.setVisible(true);
        frame.setSize(450, 500);
        frame.setLocationRelativeTo(this);


    }//GEN-LAST:event_jMenuItem3ActionPerformed

